from fastapi import FastAPI, UploadFile, File, HTTPException
from fastapi.staticfiles import StaticFiles
from fastapi.responses import HTMLResponse
import os
import json
import asyncio
from datetime import datetime
from src.core.metrics import get_metrics_snapshot
from src.core.health import get_health
from src.core.self_learning import accept_feedback
from src.core.kag_builder import KAGBuilder

app = FastAPI(title='TERAG Local Cloud', version='1.1')

# –ù–∞—Å—Ç—Ä–æ–π–∫–∞ –¥–∏—Ä–µ–∫—Ç–æ—Ä–∏–π
UPLOAD_DIR = 'data/docs'
GRAPH_FILE = 'data/indexes/graph.json'
JOURNAL_FILE = 'data/journal/journal.jsonl'

# –°–æ–∑–¥–∞—ë–º –Ω–µ–æ–±—Ö–æ–¥–∏–º—ã–µ –¥–∏—Ä–µ–∫—Ç–æ—Ä–∏–∏
os.makedirs(UPLOAD_DIR, exist_ok=True)
os.makedirs('data/indexes', exist_ok=True)

# –ü–æ–¥–∫–ª—é—á–∞–µ–º —Å—Ç–∞—Ç–∏—á–µ—Å–∫–∏–µ —Ñ–∞–π–ª—ã
app.mount("/api/static", StaticFiles(directory="src/api/static"), name="static")

@app.get("/")
def root():
    return {"message": "TERAG Local Cloud API", "version": "1.1"}

@app.get("/api/metrics")
def get_metrics():
    return get_metrics_snapshot()

@app.get("/api/health")
def get_health_status():
    return get_health()

@app.post("/api/feedback")
def api_feedback(payload: dict):
    result = accept_feedback(payload)
    return {'status': 'ok', 'accepted': result}

@app.post("/api/upload")
async def upload_document(file: UploadFile = File(...)):
    """–ó–∞–≥—Ä—É–∑–∫–∞ –¥–æ–∫—É–º–µ–Ω—Ç–∞ –¥–ª—è –æ–±—Ä–∞–±–æ—Ç–∫–∏"""
    try:
        # –°–æ—Ö—Ä–∞–Ω—è–µ–º —Ñ–∞–π–ª
        file_path = os.path.join(UPLOAD_DIR, file.filename)
        with open(file_path, 'wb') as f:
            content = await file.read()
            f.write(content)

        # –û–±—Ä–∞–±–∞—Ç—ã–≤–∞–µ–º –¥–æ–∫—É–º–µ–Ω—Ç
        async with KAGBuilder() as kag:
            result = await kag.process_document(file_path, 'markdown')

        if 'error' in result:
            raise HTTPException(status_code=500, detail=result['error'])

        return {
            'status': 'ok',
            'filename': file.filename,
            'entities_count': len(result.get('entities', [])),
            'relations_count': len(result.get('relations', [])),
            'graph_id': result.get('id')
        }

    except Exception as e:
        raise HTTPException(status_code=500, detail=str(e))

@app.get("/api/graph")
def get_knowledge_graph():
    """–ü–æ–ª—É—á–µ–Ω–∏–µ –≥—Ä–∞—Ñ–∞ –∑–Ω–∞–Ω–∏–π"""
    if not os.path.exists(GRAPH_FILE):
        return {'graph': [], 'edges': [], 'metadata': {'total_nodes': 0, 'total_edges': 0}}

    try:
        with open(GRAPH_FILE, 'r', encoding='utf-8') as f:
            data = json.load(f)

        # –î–æ–±–∞–≤–ª—è–µ–º –º–µ—Ç–∞–¥–∞–Ω–Ω—ã–µ
        data['metadata'] = {
            'total_nodes': len(data.get('graph', [])),
            'total_edges': len(data.get('edges', [])),
            'last_updated': datetime.now().isoformat()
        }

        return data

    except Exception as e:
        return {'error': str(e), 'graph': [], 'edges': []}

@app.get("/api/graph/stats")
def get_graph_stats():
    """–°—Ç–∞—Ç–∏—Å—Ç–∏–∫–∞ –≥—Ä–∞—Ñ–∞ –∑–Ω–∞–Ω–∏–π"""
    if not os.path.exists(GRAPH_FILE):
        return {'nodes': 0, 'edges': 0, 'sources': 0}

    try:
        with open(GRAPH_FILE, 'r', encoding='utf-8') as f:
            data = json.load(f)

        nodes = data.get('graph', [])
        edges = data.get('edges', [])

        # –ü–æ–¥—Å—á–∏—Ç—ã–≤–∞–µ–º –∏—Å—Ç–æ—á–Ω–∏–∫–∏
        sources = set()
        for node in nodes:
            if 'source' in node:
                sources.add(node['source'])

        return {
            'nodes': len(nodes),
            'edges': len(edges),
            'sources': len(sources),
            'density': len(edges) / max(len(nodes), 1)
        }

    except Exception as e:
        return {'error': str(e)}

@app.get("/api/ingest", response_class=HTMLResponse)
async def ingest_documents():
    """–ó–∞–ø—É—Å–∫ –∏–Ω–¥–µ–∫—Å–∞—Ü–∏–∏ –≤—Å–µ—Ö –¥–æ–∫—É–º–µ–Ω—Ç–æ–≤"""
    try:
        # –ó–∞–ø—É—Å–∫–∞–µ–º –∏–Ω–¥–µ–∫—Å–∞—Ü–∏—é –≤ —Ñ–æ–Ω–µ
        asyncio.create_task(run_ingestion())
        return "<h1>–ò–Ω–¥–µ–∫—Å–∞—Ü–∏—è –∑–∞–ø—É—â–µ–Ω–∞</h1><p>–ü—Ä–æ–≤–µ—Ä—å—Ç–µ –ª–æ–≥–∏ —Å–µ—Ä–≤–µ—Ä–∞ –¥–ª—è –ø—Ä–æ–≥—Ä–µ—Å—Å–∞.</p>"
    except Exception as e:
        return f"<h1>–û—à–∏–±–∫–∞</h1><p>{e}</p>"

async def run_ingestion():
    """–ó–∞–ø—É—Å–∫ –∏–Ω–¥–µ–∫—Å–∞—Ü–∏–∏ –≤ —Ñ–æ–Ω–µ"""
    try:
        from terag_ingest import main
        await main()
    except Exception as e:
        print(f"–û—à–∏–±–∫–∞ –∏–Ω–¥–µ–∫—Å–∞—Ü–∏–∏: {e}")

@app.get("/metrics/prometheus")
def prometheus_metrics():
    """–ú–µ—Ç—Ä–∏–∫–∏ –≤ —Ñ–æ—Ä–º–∞—Ç–µ Prometheus"""
    metrics = get_metrics_snapshot()
    lines = []
    for key in ['RSS', 'COS', 'FAITH', 'Growth', 'Resonance', 'confidence']:
        lines.append(f'ai_reps_{key.lower()} {metrics[key]}')
    return '\n'.join(lines) + '\n'
